import Foundation
import UIKit

class Move: UIViewController {

    override func viewDidLoad() {
        super.viewDidLoad()

        let x = 0
        let y = 50

        let tableView = UITableView()

        header.text = "AppCode"
        header.sizeToFit()
        let header = UILabel()

        self.view.addSubview(tableView)
        tableView.frame = CGRect(x: x, y: y, width: 320, height: 400)
        tableView.tableHeaderView = header
    }

}
