import Foundation

class FoldingDemo {

    public class func process(register: Int) {

        var counter = Array<Int>()
        counter[0] = 0;
        var closure = {
            counter[0] = counter[0] + 1;
            print("\(counter[0]) ");
        }
        checkAndStart(flag: (register > 10), closure: closure)
    }

    private class func checkAndStart(flag: Bool, closure: () -> ()) {
        if (flag) {
            closure()
        } else {
            print("suspending");
        }
    }
}
