xml.CopyText:
import Foundation

class Feature {
    var name = ""
}

protocol IDEProtocol {
    func navigation() -> Feature
    func assistance() -> Feature
    func generation() -> Feature
}

class JetBrainsIDE: IDEProtocol {
    func navigation() -> Feature {
        return Feature()
    }

    func assistance() -> Feature {
        return Feature()
    }

    func generation() -> Feature {
        return Feature()
    }
}

class AppCode: JetBrainsIDE {}
